import React, { useState, useRef, useEffect, useCallback } from "react";
import {formater} from "./../../../../../utils/time/timeFromater";
import "./progress-slider.scss"

//horizontal
const ProgressSlider = ({player, seek}) => {
    const [progress, setProgress] = useState(0);
    const [buffered, setBuffred] = useState(0);
    const [currentRecord, setCurrentRecord] = useState(null);
    // const [position,  setPosition] = useState();
    const [currentTime, setCurrentTime] = useState(0);

    const [currentDuration, setCurrentDuration] = useState(0);
    
    const sliderArea = useRef(null);
    const sliderThumb = useRef(null);
    const sliderProgress = useRef(null);
    const sliderBufferd = useRef(null);

    //dementions 
    const [sliderAreaRect, setSliderAreaRect] = useState(0)
    const [thumbRect, setThumbRect] = useState(0)

    const setThumbPosition = useCallback((progress)=>{
        let position = 0;
        position = sliderAreaRect.width * progress ;
        sliderProgress.current.style.transform = `scaleX(${position}) translateX(50%)`;
        position -= (thumbRect.width / 2) ;  
        sliderThumb.current.style.transform = `translateX(${position}px)`;
    }, [thumbRect, sliderAreaRect])

    useEffect(()=>{
        if(!sliderAreaRect){
            return;
        }
        const position = sliderAreaRect.width * buffered;
        sliderBufferd.current.style.transform = `scaleX(${position}) translateX(50%)`;
    }, [buffered, sliderBufferd, sliderAreaRect])

    useEffect(()=>{
      //  console.log(progress)
       setCurrentTime(currentDuration * progress);
       setThumbPosition(progress);
    }, [progress , setCurrentTime])

    useEffect(()=>{
        console.log(currentRecord)
        if(currentRecord){
           setCurrentDuration(currentRecord.duration);
        }

    }, [currentRecord, setCurrentDuration])

    useEffect(()=>{
        if(!player){
            return;
        }
        player.addProgressWatcher('#', setProgress);
        player.addCurrentWatcher(setCurrentRecord);
        player.addBuffredWatcher(setBuffred);
    }, [player])


    const thumbMouseDown = useCallback((e)=>{
        console.log('clikc;', e)
    }, [])

    const areaMouseDown = useCallback((e)=>{
     //   console.log(e)
        const y = e.clientY - sliderAreaRect.left;
      //  console.log( sliderAreaRect)
        const progress = y / sliderAreaRect.width;
        console.log(progress)
       // seek({progress})
    }, [sliderAreaRect, seek])

    const setThumb = useCallback((ref)=>{
        //sliderThumb(ref);
        sliderThumb.current = ref;
        setThumbRect(ref.getBoundingClientRect())
        ref.addEventListener('mousedown', (e)=> thumbMouseDown(e))
    }, [sliderThumb, setThumbRect])

    useEffect(()=>{
        if(sliderAreaRect){
            sliderArea.current.addEventListener('mousedown', areaMouseDown);
        }
    }, [sliderAreaRect])

    const setSliderArea = useCallback((ref) => {
        sliderArea.current = ref;
        setSliderAreaRect(ref.getBoundingClientRect())
    }, [sliderArea, setSliderAreaRect])

    return (
        <div className="seek-slider">
            <div className="record-details-time time-current">
                 <span>{formater.secondsToStr(currentTime / 1000)}</span>
            </div>
            <div className="slider-active-area" ref={setSliderArea}>
                <div className="slider-buffered" ref={sliderBufferd}/>
                <div className="slider-progress" ref={sliderProgress}/>
                
                <div className="slider-thumb" ref={setThumb} draggable="false"/>
            </div>
            <div className="record-details-time time-duration">
                <span>{formater.secondsToStr(currentDuration/1000)}</span>
            </div>
        </div>
    
    )
}

export default ProgressSlider;
