import React, { useEffect , useRef} from "react";
import { connect } from "react-redux";
import WaveSurfer from "wavesurfer";

class Deck extends React.Component {
    constructor(){
        super();
        this.mainWave = React.createRef();
        this.smallWave = React.createRef();
        
    }


    componentDidMount(){
        if(!this.props.active) return;
        this.bigWaveSurfer = WaveSurfer.create({
            container: this.mainWave.current,
            waveColor: 'violet',
            progressColor: 'purple',
            autoCenter : true,
            scrollParent: true,
            hideScrollbar:true,
            fillParent: false,
            height: 130,
            minPxPerSec : 150
        });

        this.smallWaveSurfer = WaveSurfer.create({
            container: this.smallWave.current,
            fillParent: true,
            scrollParent : false,
            hideScrollbar: true,
            scrollParent : false,
            height: 50,
        })

        let duration = 0;
        this.bigWaveSurfer.on('ready', () => {
            this.bigWaveSurfer.play();
            duration = this.bigWaveSurfer.getDuration();
            let resolution = 430;
            let peaks = this.bigWaveSurfer.backend.getPeaks(resolution, 0, resolution);
            this.smallWaveSurfer.load(null, peaks);
           // this.smallWaveSurfer.drawer.drawPeaks(peaks, resolution, 0, resolution);

            startSync();

        });

        let syncHandle = null;
        let startSync = () => {
            syncHandle = setInterval(() => {
                let progress = this.bigWaveSurfer.getCurrentTime() / duration;
                console.log(progress);
                this.smallWaveSurfer.backend.seekTo(this.bigWaveSurfer.getCurrentTime());;
            }, 100);
        }

        this.bigWaveSurfer.load('https://ciagnik-yt-downloader.herokuapp.com/download?url=https://www.youtube.com/watch?v=hVv331iLMXM');
    }

    render() {
        return (
            <div style={{height: 250 + 'px'}}>
                {this.props.children}
                <div ref={ this.mainWave } style={{height:70 + '%'}} >  </div>
                <div ref={ this.smallWave }  style={{height:30 + '%'}} >  </div>
            </div>
            )
        }
    
}

export default connect()(Deck);
