import React from "react";


const withRefSize = (WrappedComponent) => {

    class HocComponent extends React.Component{
        componentDidMount() {
            console.log('tiis.refs', this)
        }

        importRef = ref => {
            console.log(ref)
        }

        render(){
            const {forwardedRef , ...rest} = this.props
         //   console.log(WrappedComponent)
            let what;
            console.log("what", what)
            return <WrappedComponent {...rest} ref={forwardedRef} forwardRef={'what'} exportRef={importRef}/>
        }
    } 


    return React.forwardRef((props, ref)=>{
        console.log("ref" ,ref)
        return <HocComponent {...props} forwardedRef={ref} />
    })
}


export default withRefSize;
