    //         sliderThumb.current.ondragstart = () => false;
    //     }
    // }, [thumbRect])

    // const setThumb = useCallback((ref)=>{
    //     sliderThumb.current = ref;
    //     setThumbRect(ref.getBoundingClientRect())
    // }, [sliderThumb, setThumbRect])


