import React, { useMemo, useState, useEffect, useCallback, useContext } from "react"
import { getApi } from "./../../../../apis/apiProvider";
import {PLAYBACK_STATE} from "./ListItem/PlaybackButton/PlaybackButton";

//getRecordUrl
export const PlayerContext = React.createContext({
    btnStateHandlers: {},
    setProgressHandlers:[],
    getProgressDataHandler: [],
    progressUpdaterHandler: null, 

    setBtnCtrlHandler(id, handler){
        this.btnStateHandlers[id] = handler;
    },

    setBtnState(id, state){
        this.btnStateHandlers[id](state)
    },

    setProgressHandler(id, handler){
        if(this.setProgressHandlers[id]){
            this.setProgressHandlers[id].push(handler);
        }
        this.setProgressHandlers[id] = [handler];
    },

    setProgress(id, progress){
        for(let handler of this.setProgressHandlers[id]){
            handler(progress)
        }
    },
  
    setProgressDataHandler(id, handler){
        this.getProgressDataHandler[id] = handler;
    },

    getProgress(id){
        return this.getProgressDataHandler[id]();
    }
})

export function useRecordPlayer(){
    const [mediaElement, setMediaElement] = useState(null);
    // const [current, setCurrent] = useState(null);
    // const [currentStart, setCurrentStart] = useState(0);
    const [current, setCurrent] = useState({
        id: null,
        start: 0,
        duration: 0
    })
    const [progressUpdater, setProgressUpdater] = useState(null);
    const ctrContext = useContext(PlayerContext);

    const api = useMemo(()=>{
        return getApi('RecordsStore');
    }, [getApi])

    useEffect(()=>{
        const medEl = document.createElement('audio');
        medEl.autoplay = true;
        medEl.preload = 'metadata'
        setMediaElement(medEl)
    }, [setMediaElement])

    const startUpdateProg = useCallback((current)=>{
        const handler = setInterval(()=>{
            console.log(current)
            if(!current.id || !current.duration){
                return;
            }
            const startTime = current.start * (current.duration/1000);
            const currTime = startTime + mediaElement.currentTime;
            const progress = currTime / (current.duration/1000);
            console.log(current.id, progress)
            ctrContext.setProgress(current.id, progress);
        }, 200)
        ctrContext.progressUpdaterHandler = handler;
    }, [setProgressUpdater, mediaElement, ctrContext])

    const stopUpdateProg = useCallback(()=>{
        clearInterval(ctrContext.progressUpdaterHandler);
    }, [ctrContext])

    const playback = useCallback((id, pbState)=>{
        if(pbState === PLAYBACK_STATE.PLAY){
            const {progress, duration, filePosition} = ctrContext.getProgress(id);

            console.log("P , D , F", progress, duration, filePosition)

            mediaElement.src = api.getRecordUrl(id, filePosition);
            mediaElement.play();
            if(current.id){
                ctrContext.setBtnState(current.id, PLAYBACK_STATE.PAUSE)
            } 
            console.log(id, duration, progress);
            const curr = {
                ...current,
                id,
                duration,
                start: progress
            }
            setCurrent(curr);
            startUpdateProg(curr);
        } else if(pbState === PLAYBACK_STATE.PAUSE){
            mediaElement.pause();
            stopUpdateProg(); 
        }
    }, [
        mediaElement, 
        api, 
        current, 
        setCurrent, 
        stopUpdateProg, 
        startUpdateProg,
        progressUpdater
    ])

    const seek = useCallback(({id, filePosition, progress, duration}) => {
        stopUpdateProg();
        mediaElement.src = api.getRecordUrl(id, filePosition);
        ctrContext.setBtnState(id, PLAYBACK_STATE.PLAY);
        ctrContext.setProgress(id, progress);
        const curr = {
            id,
            start: progress,
            duration
        }
        setCurrent(curr)
        startUpdateProg(curr);
        // console.log("seek ", id , position)
    }, [stopUpdateProg, startUpdateProg, setCurrent, ctrContext, api , mediaElement])



    return [playback, seek ]
}
