import React , { useState, useCallback, useEffect, useMemo } from "react";
import BinaryButton from "./../../../BinnaryButton/BinnaryButton"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPlay, faPause } from '@fortawesome/free-solid-svg-icons'
import classNames from "classnames"
import "./playback-button.scss"

export const PLAYBACK_STATE = {
    PLAY : "play music",
    PAUSE: "pause music"
}

const PlaybackButton = ({onChange , forceState}) => {
    const [state, setState] = useState(PLAYBACK_STATE.PAUSE);
    const [icon, setIcon] = useState(faPause);

    const stateChange = useCallback((state)=>{
        const playBackState = (state) ? PLAYBACK_STATE.PLAY : PLAYBACK_STATE.PAUSE
        setState(playBackState);
        if(onChange){
            onChange(playBackState)
        }
    }, [setState, onChange])

    useEffect(()=>{
        switch(state){
            case PLAYBACK_STATE.PLAY : {
                setIcon(faPause)
                break;
            }
            case PLAYBACK_STATE.PAUSE: {
                setIcon(faPlay);
                break;
            }
        }
    }, [state, setIcon])

    useEffect(()=>{
        setState(forceState)
    }, [setState, forceState])

    const btnClassNames = useMemo(()=>{
        return classNames(
            "playback-button",
            {
                "playback-button--active": (state === PLAYBACK_STATE.PLAY)
            }
        )
    }, [state])

    return (
        <div className="playback-button-container">
            <BinaryButton className={btnClassNames}  onChange={stateChange}>
                <FontAwesomeIcon icon={icon}/>
            </BinaryButton>
        </div>
    )
}

export default PlaybackButton
