import React, { useState, useCallback } from "react";
import {reqPostComment} from "./../../../../actions";
import {connect} from "react-redux";
import Comment from "./Comment/Comment";

import {useRecordPlayer} from "./../../Hooks/useRecordPlayer";
import "./record-comments.scss";


const RecordComments = ({
        userId,
        record, 
        postComment, 
    }) =>{
    const [, player] = useRecordPlayer();

    const updateComment = useCallback((e)=>{
        setComment(e.target.value);
        if(e.target.value === ""){
            setShowButton(false)
        }
    },[setComment, setShowButton])


    const submitComment = useCallback((e) => {
        e.preventDefault();
        if(!postComment || !record || userId) return;

        const timeOfComment = 0;
        const currentPlayback = player.getCurrent();
        if(currentPlayback.id === record.id){
            timeOfComment = currentPlayback.progress * currentPlayback.duration;
        }

        const commentData = {
            recordId : record.id,
            userId: userId,
            text: comment,
            time: timeOfComment,
        }
        console.log(commentData);
        postComment(commentData)

        setComment("")
        setShowButton(false)
    },[comment, postComment, setComment, setShowButton])

    return (
        <div className="record-comments">
            {userId && <div className="comment-post-form">
                {/* <Form onSubmit={submitComment}
                    onFocus={setShowButton.bind(null, true)}
                    onBlur={onBlur}>
                    <Form.Group>
                        <Form.Label>
                            Post your comment:
                        </Form.Label>
                        <Form.Control 
                            className="record-comments-control"
                            type="textarea" 
                            as="textarea"
                            name="comment-conntent"
                            value={comment}

                            onChange={updateComment}/>
                    </Form.Group>
                    {showButton && <Button type="submit">Post</Button>}
                </Form> */}
                <Comment content={null}/>
            </div>}

                       
        </div>
    )
}
const mapStateToProps = state => ({
    record: state.records.currentRecord,
    userId: state.user.dbId,
})

const mapDispatchToProps = dispatch => ({
    postComment: (data) => dispatch(reqPostComment(data)),
    // deleteComment: (id) => dispatch(null),
    // editComment : (id, comment) => dispatch(null)
})

export default connect(mapStateToProps, mapDispatchToProps)(RecordComments)
