import store from "./../../store";
import Observer from "./observer/observer";

let mixConsole;
export default class Console{
    constructor(){
        store.subscribe(this.handleChange.bind(this));
        this.observer = new Observer(store);
        this.chanels = {
            A: null, 
            B: null
        }
    }

    static Init(){
        mixConsole = new Console();
    }

    static Get(){
        if(!mixConsole){
            mixConsole = new Console();
            return mixConsole;
        } else {
            return mixConsole;
        }
    }

    setPlayer(chanel, player){
        this.chanels[chanel] = player;
    }

    handleChange(){
        const storeChanges = this.observer.check();
        for(let diff of storeChanges){
            this.callAction(diff);
        }
    }

    callAction(diff){
        console.log(diff);
    }

}
