//--------- Displayling Records----------------

const ACTIONS = {
    RECS_REQ_USER: "Request for records of current user",
    RECS_LOAD: "Load records from request to state",
}
export { ACTIONS as RECORDS_ACTIONS };

export function reqRecOfUser(userId){
    return { type: ACTIONS.RECS_REQ_USER, userId}
}

export function loadRecords(records) {
    return { type: ACTIONS.RECS_LOAD, records }
}
