import React, {useCallback, useEffect, useMemo, useState, Fragment} from "react";
import {useHistory} from "react-router-dom";
import {set} from "lodash/object";
import UUID from "uuidjs";


const getMarkups = () => require.context('./../markups', true, /\.md$/).keys();

const Menu = () => {
    const history = useHistory();
    const [markups, setMarkups] = useState();
    const [structure, setStructure] = useState();


    useEffect(() => {
        setMarkups(getMarkups())
    }, [setMarkups])

    useEffect(() => {
        if (!markups) return;
        let _structure = {};
        markups.forEach(item => {
            let path = item.substr(2, item.length).split("/")
                .map(part => part.replace(".md", ""))
            set(_structure, path, item)
        });
        setStructure(_structure);
    }, [markups, setStructure])

    const goTo = useCallback(path => history.replace("./introduction/" + path.join("/")), [])

    const renderList = useCallback((list, path = []) => {
        const items = Object.entries(list).map(([key, value]) => {
            if (typeof value === "string") {
                path.push(key);
                return (
                    <li key={UUID.genV1()}
                        className="menu__list__item"
                        onClick={goTo.bind(null, path)}>
                            {key}
                    </li>
                )
            } else if (typeof value === "object") {
                path.push(key);
                return (
                        <Fragment key={UUID.genV1()}>
                            <li className="menu__list__item">{key}</li>
                            <ul className={"menu__list menu__list--depth-" + path.length}>
                                {renderList(value, path)}
                            </ul>
                        </Fragment>
                )
            }
        });

        return (
         <ul key={UUID.genV1()} className={"menu__list"}>
                {items}
         </ul>
        )
    }, [])

    console.log("markups", markups)
    console.log("strucure", structure)

    const content = useMemo(()=>{
        if(!structure) return null;
        return renderList(structure)
    }, [structure])

    console.log(structure);

    return (
        <aside className="introduction__menu">
            <nav className="menu">
                {content}
            </nav>
        </aside>
    )
}

export default Menu;
