export default function parse(errors){
    let result = "";
    for(let error of errors){
        if(error.path && error.path instanceof Array){
            result += "path: " + error.path.join("/") + " "
        }
        result += "message: " +error.message;
        result += "code: "  + error.extensions.code
        result += " || \n";
    }
    return result;
}
