import React from "react";
import style from "./knob.scss";



class Knob extends React.Component{

    state = {
        position: 30, // form 0 to 100
        rightArm: -60,
        leftArm: 90, // to 240
    }

    render(){
        return (
            <div className="knob">
                <div 
                    className="knob-big-circle"
                    style={{
                        backgroundImage :     
                        `linear-gradient(`+ (0 + this.state.rightArm) + `deg, `+ style.knobBackground +` 50%, transparent 50%),    
                         linear-gradient(`+ (0 + this.state.leftArm) +`deg, transparent 50%, `+ style.knobBackground +` 50%)`
                    }}
                    />
                <div className="knob-small-circle"/>
            </div>
        )
    }
}


export default Knob;
