export default class Effector{
    constructor(audioContext){
        this.mainAC = audioContext;
        this.channels = {
            1: null,
            2: null,
        }
    }

    connect(inputNodes){
        const [input1, input2] = inputNodes;

        input1.gain.setValueAtTime(0, this.mainAC.currentTime);
        input2.gain.setValueAtTime(0, this.mainAC.currentTime);

        return [input1, input2]
    }
}
