import React, { useState, useRef, useEffect, useCallback } from "react";
import {formater} from "./../../../../../utils/time/timeFromater";
import {toRange} from "./../../../../../utils/math/argRanges";
import "./progress-slider.scss"

//horizontal
const ProgressSlider = ({player, seek}) => {
    const [progress, setProgress] = useState(0);
    const [buffered, setBuffred] = useState(0);
    const [currentRecord, setCurrentRecord] = useState(null);
    const [currentTime, setCurrentTime] = useState(0);
    const [currentDuration, setCurrentDuration] = useState(0);
    const isDragged = useRef();
    const seekTarget = useRef();

    const sliderArea = useRef(null);
    const sliderThumb = useRef(null);
    const sliderProgress = useRef(null);
    const sliderBufferd = useRef(null);
    const sliderTimePreview = useRef(null);
    //dementions 
    const [sliderAreaRect, setSliderAreaRect] = useState(0)
    const [thumbRect, setThumbRect] = useState(0)

    const setThumbPosition = useCallback((progress)=>{
        let position = 0;
        position = sliderAreaRect.width * progress ;
        sliderProgress.current.style.transform = `scaleX(${position}) translateX(50%)`;
        position -= (thumbRect.width / 2) ;  
        sliderThumb.current.style.transform = `translateX(${position}px)`;
    }, [thumbRect, sliderAreaRect])

    const setProgressHandler = useCallback((progress)=>{
        if(!isDragged.current){
            setProgress(progress);
        }
    }, [isDragged, ])

    useEffect(()=>{
        if(!sliderAreaRect){
            return;
        }
        const position = sliderAreaRect.width * buffered;
        sliderBufferd.current.style.transform = `scaleX(${position}) translateX(50%)`;
    }, [buffered, sliderBufferd, sliderAreaRect])

    useEffect(()=>{
       setCurrentTime(currentDuration * progress);
       setThumbPosition(progress);
    }, [progress , setCurrentTime, isDragged])

    useEffect(()=>{
        if(currentRecord){
           setCurrentDuration(currentRecord.duration);
        }
    }, [currentRecord, setCurrentDuration])

    useEffect(()=>{
        if(!player){
            return;
        }
        player.addProgressWatcher('#', setProgressHandler);
        player.addCurrentWatcher(setCurrentRecord);
        player.addBuffredWatcher(setBuffred);
    }, [player])

    const thumbDraging = useCallback( (areaRect, e) => {
       const x = e.clientX - areaRect.left;
       let progress = x / areaRect.width;
       progress = toRange(progress, 0 ,1);
       seekTarget.current = progress;
       setProgress(progress)
    }, [])

    const thumbMouseDown = useCallback((e)=>{
        if(!currentDuration){
          //  return;
        }
        const shiftX = e.clientX - sliderThumb.current.getBoundingClientRect().left;
        const areaRect = sliderArea.current.getBoundingClientRect();
        const thumbRect = sliderThumb.current.getBoundingClientRect();
        console.log("shiftX", shiftX)
        const thumbDragingWithArgs = thumbDraging.bind(null, areaRect)
        isDragged.current = true;
        window.addEventListener('mousemove', thumbDragingWithArgs)
        const mouseup = () => {
            window.removeEventListener('mouseup', mouseup);
            window.removeEventListener('mousemove', thumbDragingWithArgs);
            isDragged.current = false;
            seek({progress : seekTarget.current});
            seekTarget.current = null;
        }
        window.addEventListener('mouseup', mouseup)
    }, [sliderThumb, sliderArea, isDragged, seekTarget])

    const areaMouseDown = useCallback((e)=>{
        if(e.target !== sliderThumb.current){
          //  return;
            console.log(e.target, sliderArea.current)
        }
        const x = e.clientX - sliderAreaRect.left  + (thumbRect.width / 2);
        let progress = x / sliderAreaRect.width  ;
        progress = toRange(progress, 0 , 1)
        seek({progress})
    }, [sliderAreaRect, seek, sliderArea])

    const areaMouseMove = useCallback( e =>{
        const x = e.clientX - sliderAreaRect.left  + (thumbRect.width / 2);
        let progress = x / sliderAreaRect.width;
        progress = toRange(progress, 0 , 1)
        const time = currentDuration * progress;
        let str = formater.secondsToStr(time / 1000);
        sliderTimePreview.current.dataset.value = time ? str : "";
        sliderTimePreview.current.style.transform = `translateX(${x}px)`
    }, [sliderAreaRect, currentDuration , sliderTimePreview])

    const areaMouseEnter = useCallback( e => {
        sliderTimePreview.current.classList.add("preview--visible")
    }, [sliderTimePreview])

    const areaMouseLeave = useCallback( e => {
        sliderTimePreview.current.classList.remove("preview--visible")
    }, [sliderTimePreview])

    useEffect(()=>{
        if(sliderAreaRect){
            sliderArea.current.addEventListener('mousedown', areaMouseDown);
            sliderArea.current.addEventListener('mouseenter', areaMouseEnter);
            sliderArea.current.addEventListener('mouseleave', areaMouseLeave);
        }
    }, [sliderAreaRect])

    useEffect(()=>{
        if(sliderAreaRect && currentDuration){
            sliderArea.current.addEventListener('mousemove', areaMouseMove);
        }
    }, [sliderAreaRect, currentDuration])

    useEffect(()=>{
        if(thumbRect){
            sliderThumb.current.addEventListener('mousedown',  thumbMouseDown);
            sliderThumb.current.ondragstart = () => false;
        }
    }, [thumbRect])

    const setThumb = useCallback((ref)=>{
        sliderThumb.current = ref;
        setThumbRect(ref.getBoundingClientRect())
    }, [sliderThumb, setThumbRect])


    const setSliderArea = useCallback((ref) => {
        sliderArea.current = ref;
        setSliderAreaRect(ref.getBoundingClientRect())
    }, [sliderArea, setSliderAreaRect])


    return (
        <div className="seek-slider">
            <div className="slider-time time-current">
                 <span>{formater.secondsToStr(currentTime / 1000)}</span>
            </div>
            <div className="slider-active-area" ref={setSliderArea} draggable="false">
                <div className="slider-buffered" ref={sliderBufferd} draggable="false"/>
                <div className="slider-progress" ref={sliderProgress} draggable="false"/>
                
                <div className="slider-thumb" ref={setThumb} draggable="false"/>
                <div className="slider-time-preview"  ref={sliderTimePreview} data-value="1"/>
            </div>
            <div className="slider-time time-duration">
                <span>{formater.secondsToStr(currentDuration/1000)}</span>
            </div>
        </div>
    
    )
}

export default ProgressSlider;
