import { createPlaylist, ACTIONS } from "../../actions";
import { takeEvery, select, put } from "redux-saga/effects"
import { getApi } from "./../../apis/apiProvider";
import query from "./../../qlQueries/createPlaylist";
import { generateTemplateName, findClosesDir } from "./../../reducers/console/playlist/utils";
import { get } from "lodash/object";
export default function* createDirRequestSaga() {
    yield takeEvery(ACTIONS.PL_CREATE_PLAYLIST_REQUEST, callApi)
}

const getToken = state => state.user.token;

const getCurrentSelection = state => state.playList.currentSelection;

const getParentPath = (state, currentSelection) =>
    findClosesDir(state, currentSelection)

const generatePlaylistName = (state, parrentPath) => 
    generateTemplateName(state.playList, parrentPath, "New Playlist");

const getParrentId = (state, path) => get(state.playList, path)._id;


function* callApi(action) {
    const { callQuery } = getApi("UserAssets");
    const token = yield select(getToken);
    const currentSelection = yield select(getCurrentSelection);
    const parrentPath = yield select(getParentPath, currentSelection);
    let playlistName = action.name;
    let renameMode = false;
    if(!playlistName){
        playlistName = yield select(generatePlaylistName, parrentPath);
        renameMode = true;
    } 
    const dirId = yield select(getParrentId, parrentPath);
    console.log("query", query(dirId, playlistName))
    let result = yield callQuery(query(dirId, playlistName), token);
    if(!result || !result.data?.createPlaylist){
        return ;
    }
    const id = result.data.createPlaylist.id;

    console.log(id, result)

    yield put(createPlaylist(playlistName, id, renameMode));
}
