import React, { useState , useEffect, useRef} from "react";
import "./pitch-control.scss";

class PitchControl extends React.Component{
    constructor() {
        super();

        this.sliderRangeElement = React.useRef(null);
        this.sliderThumbElement = React.useRef(null);
        this.sliderAreaElement = React.useRef(null);
    }

