import React from "react";
import { connect } from "react-redux"; 
import { togglePlay , toggleCue } from "./../../../actions/actions";
import { Button } from "react-bootstrap";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlay } from '@fortawesome/free-solid-svg-icons';
import "./play-back.scss";

const PlayBackControls = props => {
    console.log(props)
    return (
        <div className={"play-back-controls-" + props.name }>
            <Button className={"btn-play " + (((!props.playPressed) && "btn--pressed" ) || "" ) }
                onClick={  props.onPlayPress.bind(null, props.name) }>
                    <FontAwesomeIcon icon={faPlay}/>
            </Button>
            <Button className={"btn-cue " + ((props.cuePressed && "btn--pressed") || "") }
                onClick={ props.onCuePress.bind(null, props.name) }> 
                    Cue 
            </Button>
        </div>
    )
}

const mapStateToProps = (state, ownProps) => ({
    playPressed : state.console.chanel[ownProps.name].playBackState.paused,
    cuePressed : state.console.chanel[ownProps.name].playBackState.cueActive,
})

const mapDispachToProps = dispach => ({
    onPlayPress : (destination) => dispach(togglePlay(destination)),
    onCuePress : (destination) => dispach(toggleCue(destination))
})

export default connect(mapStateToProps, mapDispachToProps)(PlayBackControls);
