import {ACTIONS} from "./../../actions";

const initState={
    list: []
}

export default function recordsReducer(state = initState, action){
    switch(action.type){
        case ACTIONS.RECS_LOAD : {
            return {
                ...state,
                list : action.records
            }
        }

        default: return state;
    }
}
