import { setUserProfile } from "./../actions";
import { put , call} from 'redux-saga/effects';
import {getApi} from "./../apis/apiProvider";

export default function *getUserProfi(action){
    const api = getApi("UserApi");
    const query = `
        query { 
            me {
                username,
                email,
                nickname,
                familyName,
                givenName,
                picture,
            }
        }
    `;

    const result = yield call(api.callQuery, query);
    const user = result.data.me;
    yield put(setUserProfile(user))
}
