import React, {useState, useEffect}  from "react";
import {Form} from "react-bootstrap"


const RecordDetails = ({record}) => {
    const [editMode, setEditMode] = useState();

    const [title, setTitle] = useState("");
    const [description, setDescription] = useState();
    const [generes, setGeneres] = useState();
    
    const [aviableGeneres, setAviableGeneres] = useState();

    useEffect(()=>{
        if(!record) return;

        setTitle(record.title)
        setDescription(record.description)
        setGeneres(record.generes);
    },[record, setTitle, setDescription, setGeneres])

    return (
        <div className="record-details">
            <div className="record-title">
                <Form.Label>Title: </Form.Label>
                <Form.Control type="text" value={title} onChange={setTitle} disabled/>
            </div>
            <div className="record-description">
                <Form.Label>Description:</Form.Label>
                {description}
            </div>
            <div className="genere-list">
                <Form.Label>Generes:</Form.Label>
                {generes && generes.map((genere) => <div className="genere">{genere}</div>)}
            </div>
        </div>
    )
}

export default RecordDetails
