import React, {useState, useEffect}  from "react";
import {Form} from "react-bootstrap"
import "./record-details.scss";

const RecordDetails = ({record}) => {
    const [editMode, setEditMode] = useState(false);

    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [generes, setGeneres] = useState([]);
    
    const [aviableGeneres, setAviableGeneres] = useState();

    useEffect(()=>{
        if(!record) return;

        setTitle(record.title || "")
        setDescription(record.description || "Some descirpiton")
        setGeneres(record.generes || ["Techno", "Minimal", "Soul"]);
    },[record, setTitle, setDescription, setGeneres])


    const changeMode = useCallback((e)=>{
        console.log(e);
    }, [setEditMode])

    return (
        <div className="record-details">
            <Form.Check type="switch" id="switch-edit-mode" label="edit mode" className="switch-edit-mode" value={editMode} onChange={changeMode}/>
            <Form.Group className="record-title">
                <Form.Label>Title: </Form.Label>
                <Form.Control type="text" value={title} onChange={setTitle} disabled={!editMode}/>
            </Form.Group>
            <Form.Group className="record-description">
                <Form.Label>Description:</Form.Label>
                <Form.Control as="textarea" value={description} onChange={setDescription} disabled={!editMode}/>
            </Form.Group>
            <Form.Group className="genere-list">
                <Form.Label>Generes:</Form.Label>
                <Form.Control 
                    type="text"
                    value={generes.join(", ")} 
                    onChange={ v => setGeneres(v.split(","))} 
                    disabled={!editMode}/>
            </Form.Group>
        </div>
    )
}

export default RecordDetails
