import React from "react";
import WaveSurferElement from "wavesurfer";
import colors from "./../../../css/colors.scss";

const getCssColor= (variable, name) => colors[ "deck" + name.toUpperCase() + variable ]; 

const masterConfig = (container, height, name) => ({ // name deck A or B 
    container: container,
    waveColor: getCssColor('Primary', name) || 'white',
    progressColor: getCssColor('PrimaryDark', name) || 'gray',
    autoCenter : true,
    scrollParent: true,
    hideScrollbar:true,
    fillParent: false,
    height: height,
    minPxPerSec : 150
})

const slaveConfig = (container, height, name) => ({
    container: container,
    waveColor: getCssColor('Primary', name) || 'white',
    progressColor: getCssColor('PrimaryDark', name) || 'gray',
    fillParent: true,
    scrollParent : false,
    hideScrollbar: true,
    scrollParent : false,
    height: height,
})

const config = (type, container, height, name) => {
    switch(type){
        case "master" : return masterConfig(container, height, name);
        case "slave" : return slaveConfig(container, height, name );
        default : return {};
    }
}



class WaveSurfer extends React.Component {
    constructor(){
        super();
        this.waveSurfer = null;
        this.container = React.createRef();
    }

    componentDidMount(){
        this.waveSurfer = WaveSurferElement.create(config(
            this.props.type, 
            this.container.current,
            this.props.height, 
            this.props.name,
            ));

        if(this.props.type === "master" && this.props.url){
            this.waveSurfer.load(this.props.url);
        } else if (this.props.type === "slave" && this.props.peaks){
            this.waveSurfer.load(null, this.props.peaks);
        }

        //    this.props.handlers?.pushPeaks()

    }

    shouldComponentUpdate(){

    }

    render(){
        return (
            <div ref={ this.container } style={{ height: this.props.height + "px" }} >  </div>
        )
    }
};

export default WaveSurfer;
