import React, { Fragment } from "react";
import { connect } from "react-redux";
import UUID from "uuidjs";
import { ContextMenuTrigger } from "react-contextmenu"
import PlaylistTable from "./PlaylistTable/PlaylistTable";
import PlaylistItem from "./Playlist_Item/PlaylistItem";

import "./play-list.scss";
import CtxMenu from "../../common/ContextMenu/ContextMenu";
import PlaylistCtx from "./PlaylistContext";

class PlayList extends React.Component {

    headers = ['Source', 'Title', 'Quality', 'Time', 'Bpm', 'Destination'];

    state = {
        currentHover : null
    }

    render() {
        return (
            <Fragment>
                <div className="playlist">
                    <ContextMenuTrigger id="playlist_ctx_menu" className="playlist">
                        <PlaylistCtx.Provider value={this.state}>
                            <PlaylistTable headers={this.headers}>
                                {this.props.playList && this.props.playList.map((item, index) =>
                                    <PlaylistItem item={item} listIndex={index} key={UUID.genV1()} />
                                )}
                            </PlaylistTable>
                        </PlaylistCtx.Provider>
                    </ContextMenuTrigger>
                </div>
                <CtxMenu id="playlist_ctx_menu"
                    items={{
                        "Send to A": null,
                        "Send to B": null,
                        "Calc BPM": null,
                        "Delete": null
                    }}
                />
            </Fragment>
        )
    }
}

const mapStateToProps = (state) => ({
    playList: state.playList.list
})

export default connect(mapStateToProps)(PlayList);
