import React from "react";
import style from "./knob.scss";



class Knob extends React.Component{

    state = {
        position: 50, // form 0 to 100
        snapShotPostion: 50,//

        rightArm: -60, // form -60 to 90 - this is midle
        leftArm: 90, // from 90 to 240 
    }

    snap(){
        this.setState({...this.state, snapShotPostion: this.state.position})
    }

    mouseDown(event){
        const startY = event.clientY;
        let mouseMove = this.mouseMove.bind(this, startY);
        this.snap();
        document.body.addEventListener('mousemove', mouseMove)
        document.body.addEventListener('mouseup', e => {
            document.body.removeEventListener('mousemove', mouseMove)
        })
    }

    mouseMove(startY, event){
        let position = this.state.snapShotPostion + (event.clientY  - startY);
        this.setState({...this.state, position: position})
        console.log('clintY', position);
        //console.log('startY', startY)
    }


    mapPostionToArms(position){

    }

    render(){
        return (
            <div 
                className="knob"
                onMouseDown={this.mouseDown.bind(this)}
                onDragStart={ e => e.preventDefault()}>
                <div 
                    className="knob-big-circle"
                    style={{
                        backgroundImage :     
                        `linear-gradient(`+ this.state.rightArm + `deg, `+ style.knobBackground +` 50%, transparent 50%),    
                         linear-gradient(`+  this.state.leftArm +`deg, transparent 50%, `+ style.knobBackground +` 50%)`
                    }}
                    />
                <div className="knob-small-circle"/>
                {this.state.position}
            </div>
        )
    }
}


export default Knob;
