export default class Mixer{
    constructor(channels){
        this.channels = channels;
    }


    setGain(channelName, gainValue){
        if(gainValue === 0) return; // ideal run
        console.log(gainValue)
        let audioCtx = this.channels.getChannel(channelName).backend.ac;
        let gainNode = audioCtx.createGain();
        gainNode.gain.setValueAtTime( parseFloat(gainValue/100) , audioCtx.curretTime);
        this.channels[channelName].backend.setFilter(gainNode);
    }
}
