import {  ACTIONS } from "./../actions/actions.js";
initState = {
    leftTrack : {
        title, 
        image,
        url
    },
    rightTrack : {
        title, 
        image,
        url
    }
}
function trackReducer(state = initState, action){
    switch(action.type){
        case ACTIONS.ADD_LEFT_TRUCK : {
            return {
                ...state,
                leftTrack : {
                    ...state.leftTrack, 
                    ...action.data
                }
            }
        }
        case ACTIONS.ADD_RIGHT_TRUCK : {
            return {
                ...state,
                rightTrack : {
                    ...state.rightTrack, 
                    ...action.data
                }
            }
        }
    }
}

export default trackReducer;
