import React, {useState, useEffect, useCallback}  from "react";
import {Form , Button} from "react-bootstrap"
import "./record-details.scss";

const RecordDetails = ({record}) => {
    const [editMode, setEditMode] = useState(false);

    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [generes, setGeneres] = useState([]);
    
    const [aviableGeneres, setAviableGeneres] = useState();

    useEffect(()=>{
        if(!record) return;

        setTitle(record.title || "")
        setDescription(record.description || "Some descirpiton")
        setGeneres(record.generes || ["Techno", "Minimal", "Soul"]);
    },[record, setTitle, setDescription, setGeneres])


    const toggleMode = useCallback(()=>{
        setEditMode( value => !value )
    }, [setEditMode])

    const updateTitle = useCallback((event) => {
        setTitle(event.target.value);
    }, [setTitle])

    const updateDescription = useCallback( event => {
        setDescription(event.target.value)
    },[setDescription])
            
    const updateGeneres = useCallback( event => {
        let generes = event.target.value.split(",").map( genere => genere.trim())
        setGeneres(generes)
    }, [setGeneres])
        
    const updateRecordDetails = useCallback( event => {
        event.preventDefault();
        console.log(event);

        setEditMode(false)
    }, [setEditMode])

    return (
        <Form className="record-details" onSubmit={updateRecordDetails}>
            <Form.Check type="switch" id="switch-edit-mode" label="edit mode" className="switch-edit-mode" value={editMode} onChange={toggleMode}/>
            <Form.Group className="record-title">
                <Form.Label>Title: </Form.Label>
                <Form.Control type="text" value={title} onChange={updateTitle} disabled={!editMode}/>
            </Form.Group>
            <Form.Group className="record-description">
                <Form.Label>Description:</Form.Label>
                <Form.Control as="textarea" value={description} onChange={updateDescription} disabled={!editMode}/>
            </Form.Group>
            <Form.Group className="genere-list">
                <Form.Label>Generes:</Form.Label>
                <Form.Control 
                    type="text"
                    value={generes.join(", ")} 
                    onChange={ updateGeneres } 
                    disabled={!editMode}/>
            </Form.Group>
            {editMode && <Button type="submit"> Save </Button>}
        </Form>
    )
}

export default RecordDetails
