import React from "react";

const testimonialsDirs = './testimonials'
const getTestimonials = () => {
    function importAll (r) {
        return r.keys();
    }
    return importAll(require.context(testimonialsDirs, true, /\.json$/));
}

const Testimonial = ({
    stars,
    text,
    author: {
        name: authorName,
        position: authorPosition
    } = {}
}) => {
    const testimonials = getTestimonials().map( file => {
        return import(testimonialsDirs + file);
    });

    console.log(Promise.all(testimonials))
    return (
        <div className="home-testmionial">
            <div className="testimonial-slider">

            </div>
            <div className="testimonail-slider-control">

            </div>
        </div>
    )
}

export default Testimonial;
