export default class Mixer{
    constructor(channels){
        this.channels = channels;
    }


    setGain(channelName, gainValue){
        if(gainValue === 0) return; // idl run
        console.log(gainValue,  parseFloat(gainValue/100) )
        let audioCtx = this.channels.getChannel(channelName).backend.ac;
        console.log(audioCtx)
        let gainNode = audioCtx.createGain();
        gainNode.gain.setValueAtTime(1, audioCtx.currentTime)
        this.channels.getChannel(channelName).backend.setFilter(gainNode);
    }
}
