  render() {
    const listItems = this.state.lengths.map((length, index) => {
        let display = length;
        if (length < 1) {
            display = "1/" + 1 / length;
        }

        return (<li className="slider-list-item" 
                    key={index} 
                    data-value={length}>
                        {display}
                </li>);
    });

    return (
      <div className={"looper looper-deck-" + this.props.name}>
        <Button className="btn-in">IN</Button>
        <Button className="btn-out">OUT</Button>
        <MiniSlider
          onChange={this.sliderValueChange}
          className={"mini-slider-deck-" + this.props.name}
          renderItems={listItems}
          initValue={8}
        />
      </div>
    );
  }
}

export default Looper;
