const recorder_api_url = process.env.REACT_APP_RECORDER_API


export default {
    getSocketUrl: ()=>{
        return recorder_api_url.replace('http', 'ws').replace('https', 'wss')
    },

    getRecord: (id) =>{
        return recorder_api_url + "/records/" + id;
    }
}
