import React, { useMemo, useEffect } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { reqUserProfile } from "./../../../../actions";
import "./user-profile.scss"

const UserProfile = ({ nickname, profile, reqProfile }) => {

    useEffect(() => {
        if (nickname && !profile) {
            reqProfile(nickname);
        }   
    }, [nickname, profile, reqProfile])

    if (!picture || !nickname) {
        return null;
    }

    return (
        <div className="user-profile">
            <div className="user-picture">
                <img src={picture} alt="User picture" />
            </div>
            <main>
                <div className="user-nickname">
                    <h4>{nickname}</h4>
                </div>
                <div className="user-description">
                    <p>Profile descirption, i'm a dj not a cat Profile descirption, i'm a dj not a cat</p>
                </div>
                <div className="user-record-stat">
                    <span>2 records</span>
                    <span>230 min</span>
                </div>
                <div className="generes">
                    <ul>
                        <li><Link to="/">Techno</Link></li>
                        <li><Link to="./">Minimal</Link></li>
                        <li><Link to="/">Techno</Link></li>

                    </ul>
                </div>
            </main>
        </div>
    )
}

const mapStateToProps = state => ({
    profile: state.profile.current
})

const mapDispatchToProps = dispatch => ({
    reqProfile: (nickname) => dispatch(reqUserProfile(nickname))
})

export default connect(mapStateToProps)(UserProfile);
