import React, {useState, useCallback, useMemo} from "react";
import IconBar from "./../../IconBar/IconBar"
import {useFormatRelative} from "./../../../Hooks/useFormatDate";
import "./comment.scss";
import {Form, Button} from "react-bootstrap";

const CommentContent = ({
        header,
        text, 
        date:timestamp, 
        editable,
        onChange,
        onDelete = ()=> null, 
        creationMode}) => {
    const [content, setConntent] = useState(text || "")
    const [editMode, setEditMode] = useState(false);

    const [formatRelative] = useFormatRelative();
    const dateFormated = useMemo(()=>{
        console.log("time stamp", timestamp)
        return formatRelative(timestamp, {timezone: true})
    },[timestamp, formatRelative])


    const updateComment = useCallback((e)=>{
        setConntent(e.target.value);
        if(e.target.value === ""){
            setEditMode(false)
        }
    },[setConntent, setEditMode])
    
    const onBlur = useCallback(()=>{
        (content === "") ? setEditMode(false) : setEditMode(true);
    },[content, setEditMode])


    const onSubmit = useCallback((e)=>{
        e.preventDefault();
        if(!onChange) return;
        if(creationMode){
            setConntent("")
        }
        setEditMode(false)
        onChange(content)
    }, [content, onChange, setConntent, setEditMode])

    return (
        <Form onSubmit={onSubmit}
            onFocus={setEditMode.bind(null, true)}
            onBlur={onBlur}>
            <Form.Group>
                <Form.Label>
                    { header }
                </Form.Label>
                {dateFormated}
                { editable && !creationMode &&
                        <IconBar items={{
                            edit: setEditMode.bind(null, true),
                            delete: onDelete.bind(null)
                        }}
                        />}
                <Form.Control 
                    className="record-comments-control"
                    type="textarea" 
                    as="textarea"
                    name="comment-conntent"
                    disabled={!editMode && !creationMode}
                    value={content}
                    onChange={updateComment}/>
            </Form.Group>
            {editMode && <Button type="submit">Post</Button>}
        </Form>
    )
}

export default CommentContent;
