import React from "react";
import { connect } from "react-redux";
import { Button } from 'react-bootstrap';
import { setIsSync, setIsMaster } from './../../../actions';
import "./sync-bar.scss";

const SyncBar = props =>{

    return (
        <div className={"sync-bar " + "bar-deck-" + props.name }>
            <Button className="sync-btn">Sync</Button>
            <Button className="master-btn btn--pressed ">Master</Button>
            
        </div>
    )
}

const mapStateToProps = (state, ownProps) => ({
    syncState : state.console.channel[ownProps.name].playBackState.isSync,
    masterState : state.console.channel[ownProps.name].playBackState.isMaster,
})

const mapDispachToProps = (dispach, ownProps) => ({
    setSyncState : (value) => dispach(setIsSync(ownProps.name, value)),
    setMasterState : (value) => dispach(setIsMaster(ownProps.name, value))
})

export default connect(mapStateToProps, mapDispachToProps)(SyncBar);
