import React, { useMemo, useCallback } from "react"
import {Button} from "react-bootstrap";
import "./pagin.scss";

const Pagin = ({current, all , wide = 2, call}) => {

    const button = useCallback((index, text)=>{
        return <Button 
                    key={index}
                    className={"pagin-btn" + ((index === current) ? " current" : "")} 
                    onClick={call && call.bind(null, index)}>
                        {(text || index)}
                </Button>
    },[call, current])

    const buttons = useMemo(()=>{
        let curr = button(current);
        let pre = [];
        let next =[]
        if(current > 0){
            let i;
            for(i = current -1; i > 0 ; i--){
                pre.unshift(button(i))
            }
            if(i > 0){
                pre.unshift(button(0, "<<"))
            }
        }
        if(current < all){
            let i;
            for(i = current + 1; (i <= all) && (i < i + wide) ; i++){
                next.push(button(i))
            }
            if(i <= all ){
                next.push(button(all, ">>"))
            }
        }

        return [...pre, curr, ...next]
    }, [current, all, wide])    

    return (
        <div className="paggin">{buttons}</div>
    )
}

export default Pagin
