import React from "react"
import Console from "./../../../../core/console/console";

class PeakLevelMater extends React.Component{

    state = {
        ledStates : (new Array(16)).fill(false),
        mixerChannelInterface : Console.Get().getMixerChannelInterface(this.props.name),

    }

    componentDidMount(){
        let updateLedStates = () =>{
            let frequenstySum = this.state.mixerChannelInterface.getFrequenycySum();
            console.log(frequenstySum);
            requestAnimationFrame(updateLedStates);
        }
        
        updateLedStates();
    }
    
    render(){
        return (
            <div className="peak-level-meter">
                { this.state.ledStates.map((index, ledState)=>{
                     <div className={"level-meter-led led-"+ index + ((ledState) ? " led-on" : " led-off") }/>
                })}
            </div>
        )
    }

}

export default PeakLevelMater;
