import React, { useCallback } from "react";
import { connect } from "react-redux";
import "./explorer-tree.scss";
import { isEmpty } from "./../../../../../../utils/objects/helpers";
import UUID from "uuidjs";

const ExplorerTree = props => {

    const getCloseDirElement = useCallback((name, path) =>{
         return <li data-path={path.join("/")}>{name}</li>
    })
    console.log(props.root)

    const renderDir = (name, content)=>{
        switch (typeof content) {
            case Object: {
                if(content._open)
                break;
            }
            case Array: {

                break;
            }
        }
    }

    const buildTree = (subDir, path) => {
        Object.entries(subDir).map(([name, content]) => {
            if(name === "_open"){
                return null;
            }
            return (
                <ul className="dir-content">
                    { renderDir(name, content) }
                </ul>
            )
        })
    }

    return (
        <div className="explorer-tree">
            {buildTree(props.root, [])}
        </div>
    )
}

const mapStateToProps = state => ({
    root: state.playList.root,
})

export default connect(mapStateToProps)(ExplorerTree);
