import React, { useMemo, useState, useEffect, useCallback } from "react"
import { getApi } from "./../../../../apis/apiProvider";

//getRecordUrl
export function useRecordPlayer(){
    const [btnStore, setBtnControl] = useState(null);
    const [mediaElement, setMediaElement] = useState(null);

    const api = useMemo(()=>{
        return getApi('RecordsStore');
    }, [getApi])

    useEffect(()=>{
        const medEl = document.createElement('audio');
        setMediaElement(medEl)
    }, [setMediaElement])

    const playback = useCallback((id, playbackState)=>{
        console.log(id, playbackState);
        mediaElement.src = api.getRecordUrl(id); 
    }, [mediaElement, api])

    function seek(id, position){
        console.log("seek ", id , position)
    }

    function btnRef(id, ref){
        setBtnControl({
            ...btnStore,
            [id]: () => ref()
        })
    }

    return [playback, seek, btnRef]
}
