const map = require('unist-util-map');
const vFile = require('vfile');

module.exports = attacher

const newFileFrom = (vfile, contents) => {
    return vFile({...vfile, contents: contents})
}

function attacher() {
   // console.log("the this", this.Parser);
    const pattern = /{\.(\w+)}/;
    const processor = this;
    function transformer(tree, file) {
        let inlineMethods =processor.Parser.prototype.inlineMethods;
        let blockMethods = processor.Parser.prototype.blockMethods;

     //   console.log(file);
      //  console.log(inlineMethods)
        //console.log(tree);
        return map(tree, (node , index, parent)=> {
            if(node.type === "text" || !node.children) return node;
            const _node = node;
            parent = node;
            let children = node.children.map( node => {

                if(node.type !== "text") return node;
                console.log("node in", node)
                let match = pattern.exec(node.value);
                if(!match) return node;
                const value = node.value.replace(match[0], "");
                const subNodes = processor.parse(newFileFrom(file, value))?.children;
               // console.log("parnet subnodes", parent, subNodes)
                if(subNodes.some( subNode => subNode.type === "thematicBreak")
                    && parent.type === "paragraph"){
                    parent = subNodes.find( subNode => subNode.type === "thematicBreak");
                    parent.className = match[1];
                    parent.position = _node.position;
                    parent.position.end.column-=match[0].length;
                    parent.position.end.offset-=match[0].length;
                }
            })
            node = parent;
            node.children = children;
            if(node.type === "thematicBreak" || node.type === "text"){
                delete node.children;
            }

            console.log("node before modification", _node)
            console.log("node before return", node)
            return node;
        })
    }

    return transformer
}
