import React, {useEffect, useState} from "react";
// import ReactMarkdown from "react-markdown";
import "./content.scss";
import usePath from "../Hooks/usePath";
// import parseCssClass from "../MarkDonwExtesnion/parseCssClass2";
// import transformTree from "./../MarkDonwExtesnion/reamark-hclass";
import remarkAttr from "./../MarkDonwExtesnion/remark-attr";

const getMarkups = () => require.context('./../markups', true, /\.*md/).keys()

const load = (module) => import(`./../markups/${module}`).then( module => module.default);

const Content = () => {
    const [currentPath] = usePath();
    const [md, setMd] = useState(null);
    const [content, setContent] = useState(null);

    useEffect(()=>{
        if(!currentPath.length) return;
        let pattern = "";
        currentPath.forEach( slug => pattern += "\\/?\\d?\\." + slug)
        pattern += "\\.md";
        let regex = new RegExp(pattern);
        let file = getMarkups().filter( path => regex.test(path))[0]?.replace("./", "");
        if(!file) return;
        load(file)
            .then(md => fetch(md))
            .then(response => response.text())
            .then(md => setMd(md))
    },[currentPath, setMd])

    useEffect(()=>{
        
    }, [content, setContent])

    if(!md|| content) return null;

    return (
        <main className="introduction__content">
            {/*<ReactMarkdown source={md} plugins={[remarkAttr]}/>*/}
        </main>
    )
}

export default Content;
