import { renameSelected, ACTIONS } from "../../actions";
import { takeEvery, select, put, fork } from "redux-saga/effects"
import { getApi } from "./../../apis/apiProvider";

import { get } from "lodash/object";
import { 
    generateTemplateName as generateName, 
    findClosesDir    
    } from "./../../reducers/console/playlist/utils"

export default function* renameSelectedRequestSaga() {
    yield takeEvery(ACTIONS.SET_BPM_AND_OFFSET, handel)
}

function* handel(action){
    if(typeof action.bpm === "number"){
        yield fork(sendDataToApi, action)
    }
}

const getToken = state => state.user.token;



function* sendDataToApi(action) {
    const { callQuery , queries } = getApi("UserAssets");
    const token = yield select(getToken);

    if(token){
        try{
            const result = yield callQuery(queries.updateTrackQl, {
                id: action.id,
                bpm: action.bpm,
                offset: action.offset
            })
            if(result.errors){
                throw new Error(JSON.stringify(result.errors));
            }
        } catch (err){
            console.log("Can't update bpm in database")
            console.log(err.message);
        }
    }

}
