import STATUS from "./../STATUS";

export default function effectParam(prev, current) {
    prev = prev.effector;
    current = current.effector;

    if ((current.lastChange.sygnature !== prev.lastChange.sygnature) ||
        (current.lastChange.value !== prev.lastChange.value)) {
            switch(current.lastChange.sygnature){
                case "#preGain": {
                    return {
                        generalStatus: "MASTERING",
                        status: STATUS.MASTER_PRE_GAIN_CHANGE,
                        value: current.lastChange.value,
                    }
                }
                case "#postGain": {
                    return {
                        generalStatus: "MASTERING",
                        status: STATUS.MASTER_POST_GAIN_CHANGE,
                        value: current.lastChange.value,
                    }
                }
                case "#threshold": {
                    return {
                        generalStatus: "MASTERING",
                        status: STATUS.MASTER_THRESHOLD_CHANGE,
                        value: current.lastChange.value,
                    }
                }
                case "#ratio": {
                    return {
                        generalStatus: "MASTERING",
                        status: STATUS.MASTER_RATIO_CHANGE,
                        value: current.lastChange.value,
                    }
                }
                case "#attack": {
                    return {
                        generalStatus: "MASTERING",
                        status: STATUS.MASTER_ATTACK_CHANGE,
                        value: current.lastChange.value,
                    }
                }
                case "#release": {
                    return {
                        generalStatus: "MASTERING",
                        status: STATUS.MASTER_RELEASE_CHANGE,
                        value: current.lastChange.value,
                    }
                }
                default: return null;
            }
        }
    else {
        return null;
    }
}
