import STATUS from "./../STATUS";

export default function recState(prev, current) {
  prev = prev.record;
  current = current.record;
  let response = [];

  if(prev.recording != current.recording){
      if(current.recording){
          response.push({
              status: STATUS.RECORD_START,
              recName : current.recName,
              recId : current.recId
          })
      } else {
          response.push({
              status: STATUS.RECORD_END
          })
      }
  }

  return response;
}
