import React, { useState, useRef, useEffect } from "react";
import WaveSurferElement from "wavesurfer";
import colors from "./../../../css/colors.scss";

const getCssColor= (variable, name) => colors[ "deck" + name.toUpperCase() + variable ]; 

const masterConfig = (container, height, name) => ({ // name deck A or B 
    container: container,
    waveColor: getCssColor('Primary', name) || 'white',
    progressColor: getCssColor('PrimaryDark', name) || 'gray',
    autoCenter : true,
    scrollParent: true,
    hideScrollbar:true,
    fillParent: false,
    height: height,
    minPxPerSec : 150
})

const slaveConfig = (container, height, name) => ({
    container: container,
    waveColor: getCssColor('Primary', name) || 'white',
    progressColor: getCssColor('PrimaryDark', name) || 'gray',
    fillParent: true,
    scrollParent : false,
    hideScrollbar: true,
    scrollParent : false,
    height: height,
})

const config = (type, container, height, name) => {
    switch(type){
        case "master" : return masterConfig(container, height, name);
        case "slave" : return slaveConfig(container, height, name );
        default : return {};
    }
}



const  WaveSurfer = (props) => {

    let [ waveSurfer, setWaveSurfer ] = useState(null);
    let container = useRef(null);


    
    useEffect( ()=> {
        if(!waveSurfer){
            setWaveSurfer(WaveSurferElement.create(config(
                props.type, 
                container.current,
                props.height, 
                props.name,
                )))
        } else {
            let duration = 0; 
            const startUpdating = () => setInterval(()=>{props.exportProgressHandler(waveSurfer.getCurrentTime()/duration)}, 1000);
            waveSurfer.on('ready', ()=> {
                waveSurfer.play();
                if(props.exportPeaksHandler){
                    duration = waveSurfer.getDuration();
                    let resolution = 430;
                    let peaks = waveSurfer.backend.getPeaks(resolution, 0, resolution);
                    props.exportPeaksHandler(peaks);
                }
                if(props.exportProgressHandler){
                    startUpdating();
                } 
            })

            if(props.exportSeekToHandler){
                waveSurfer.on('seek', (progress) => props.exportSeekToHandler(progress))
            }
        }
    }, [props.type,
        props.height, 
        props.name, 
        props.exportPeaksHandler,
        props.exportProgressHandler,
        props.exportSeekToHandler,
        waveSurfer]);

    useEffect( () => {
        if(waveSurfer && props.type === "master" && props.url !== "" ){
            waveSurfer.load(props.url);
        }
    }, [props.url, waveSurfer])

    useEffect( () => {
        if(waveSurfer){
            waveSurfer.load(null, props.peaks);
        }
    }, [props.peaks, waveSurfer])

    useEffect( ()=>{
        if(props.progress && waveSurfer){
           waveSurfer.drawer.progress(props.progress);
        }
    }, [props.progress, waveSurfer])

    useEffect( ()=>{
        console.log('seekd to ', props.seekTo)
        if(props.seekTo && waveSurfer){
            waveSurfer.seekTo(props.seekTo);
        }
    }, [props.seekTo, waveSurfer])


    return (
        <div ref={ container } style={{ height: props.height + "px" }} >  </div>
    )
    
};

export default WaveSurfer;
