import React, {useCallback} from "react";
import UUID from "uuidjs";
import style from "./slider.scss"

const Slider = ({slides, next, prev, minSlideWidth = style.minSlideWidth}) => {
    const container = useRef();
    const _containerWidth = useRef();

    const containerWidth = useCallback(()=>{
        if(!container.current) return 0;
        if(!_containerWidth.current){
            _containerWidth.current = container.current.getBoundingClientRect().width;
        }
        return  _containerWidth.current;
    }, [_containerWidth])

    return (
        <ul className="carousel-slider" ref={container}>
            {slides && slides instanceof Array && slides.map( slide =>(
                <li key={UUID.genV1()} className={"slide"}>{slide}</li>
            ))}
        </ul>
    )
}
export default  Slider;
