import { ACTIONS } from "../actions/actions";

const initState = {
    list: []
}

function playListReducer(state = initState, action){
    switch(action.type){
        case ACTIONS.PUSH_TRACK : {
            return {
                ...state,
                list : Array.from(state.list).push(action.track)
            }
        }
        default : return state;
    }
}

export default playListReducer;
