import React, { useEffect, useState, useCallback } from "react";
import WaveSurfer from "wavesurfer";
import style from "./wave-surfer-player.scss";

const WaveSurferPlayer = ({item : {peaks, id, duration, fileSize}, seek ,  playerBus}) => {
    const [container, setContainerRef] = useState(null);
    const [ws, setWs] = useState(null);
    const [progress, setProgress] = useState(0);
    const [loaded, setLoaded] = useState(false);

    const onContainerRef = useCallback( (ref) => {
        setContainerRef(ref);
    }, [setContainerRef])

    useEffect(()=>{
        if(container && !loaded){
            const ws = new Promise((res, rej)=>{
                try{
                    const ws = WaveSurfer.create({
                        container: container,
                        barWidth: 2,
                        height: 100,
                        waveColor: style.waveColor,
                        progressColor: style.progressColor,
                        barMinHeight: 0,
                        hideScrollbar: true,
                    })
                    res(ws)
                } catch (err){
                    rej(err)
                }
            })
            ws.then( ws => {
                setWs(ws);
                return ws;
            })

            ws.then((ws) =>{
                if(peaks){
                    let width = container?.getBoundingClientRect().width;
                    const start = 0;
                    const end = peaks.length / 2;
                    if(window.devicePixelRatio){
                        width *= window.devicePixelRatio;
                    }
                    ws.drawer.drawPeaks(peaks, width, start, end);
                    setLoaded(true)
                }
                if(seek){
                    ws.on('seek', (progress)=>{
                        seek({
                            id, 
                            progress,
                            duration,
                        })
                        ws.drawer.progress(progress);
                    })
                }
            })
        }
    }, [container, peaks, setWs ])

    useEffect(()=>{
        if(ws && progress){
            ws.drawer.progress(progress);
        }
        console.log(progress)
    },[ws, progress])

    const getProgressData = useCallback(()=>{
        return {
            duration,
            progress,
        }
    }, [duration, progress, fileSize])

    useEffect(()=>{
        if(!playerBus){
            return;
        }
        playerBus.addProgressWatcher(id, setProgress)
    }, [playerBus])

    useEffect(()=>{
        if(!playerBus){
            return;
        }
        playerBus.addProgressProvider(id, getProgressData);
    }, [progress, playerBus])

    return (
        <div key={"ws"+id} className="ws-player-container" ref={onContainerRef}/>
    )
}

export default WaveSurferPlayer
