export default class Effector{
    constructor(audioContext){
        this.mainAC = audioContext;
        this.channels = {
            1: null,
            2: null,
        }
    }

    connect(inputNodes){
        const outputs = inputNodes.map( () => this.mainAC.createGain() )

        inputNodes.forEach( (inputNode, index) => {
            inputNode.connect(outputs[index])
        });

        return outputs;
    }
}
