import getHeader from "./../../utils/auth/headers";
const url = process.env.REACT_APP_USER_API;


export async function callQuery(query){
    const authHeader = await getHeader();
    const response = await fetch(url, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          
        },
        body: JSON.stringify({
          query: `query { todoItems { id text completed } }`,
        }),
      })
}
