import React, {useState, useEffect, useRef, useCallback} from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faVolumeUp, faVolumeMute  } from '@fortawesome/free-solid-svg-icons'
import "./volume.scss"

const Volume = props => {
    const [level , setLevel] = useState(100);
    const [icon , setIcon] = useState(faVolumeUp);
    const sliderGuide = useRef();

    useEffect(()=>{
        if(level === 0){
            setIcon(faVolumeMute)
        } else {
            setIcon(faVolumeUp)
        }
    }, [level])

    const hookUpClick = useCallback((ref)=>{
        ref.addEventListener('mousedown', (e)=>{
            console.log(sliderGuide.current)
            const rect = sliderGuide.current.getBoundingClientRect();
            const level = (rect.height -  (e.clientY - rect.top)) / rect.height
            console.log(rect.height, level)
        })
    }, [sliderGuide])

    return (
        <div className="volume">
            <button className="volume-btn">
                <FontAwesomeIcon icon={icon}/>
            </button>
            <div className="volume-slider" ref={hookUpClick}>
                <div className="volume-slider-guide" ref={sliderGuide}/>
                <div className="volume-slider-progress"/>
                <div className="volume-slider-thumb"/>
            </div>
        </div>
    )
}

export default Volume;
